static void DisplayMaze(void)
{
	glClear(GL_COLOR_BUFFER_BIT);

	glColor3f(0.5,0.5,0.5 );

	/*glPointSize(5.0);
	glBegin(GL_POINTS);
		glColor3f(0.5,0.5,0.5 );
		glVertex2f(-0.85,0.85);
	glEnd();*/
	
	for(int i = 0; i < MAZE_WIDTH; i++)
	{
		for(int j = 0; j < MAZE_HEIGHT; j++)
		{
			if(mazedata[j][i] == 'H')
			{
				glRectf(-1+i*MAZE_EACH, 1-j*MAZE_EACH-MAZE_EACH, -1+i*MAZE_EACH+MAZE_EACH, 1-j*MAZE_EACH); 
			}
		}
		//cout<<endl;
	}
	
	glFlush();
}